import styled from 'styled-components';

export const AboutFooterStyled = styled.div`
	width: 100%;
	display: flex;
	flex-direction: row;
	align-items: flex-start;
	justify-content: space-between;

	div {
		display: flex;
		flex-direction: column;
		width: 40%;

		position: relative;
		margin: 0 2.5% 0 2.5%;
		padding: 0;

		h1 {
			font-size: ${({ theme }) => theme.header};
			text-align: left;
			text-transform: uppercase;
			margin: 0;
			margin-bottom: 100px;
		}

		h2 {
			font-size: ${({ theme }) => theme.subHead};
			text-align: left;
			text-transform: capitalize;
			margin: 0;
			margin-bottom: 20px;
		}

		p {
			font-size: ${({ theme }) => theme.paragraph};
			text-align: left;
			text-transform: capitalize;
			margin: 0;
			margin-bottom: 10px;
		}
	}
`;
