import { motion } from 'framer-motion';
import { SingleMusicItemStyled } from './Music.styled';

const variants = {
	open: { opacity: 1, x: 0 },
	closed: {
		opacity: 0,
		x: '-100%',
		transition: {
			delay: 0.5,
			type: 'spring',
			stiffness: 400,
			damping: 40
		}
	}
};

const SingleMusicItem = (props) => {
	return (
		<SingleMusicItemStyled open={props.open} animate={props.isOpen ? 'open' : 'closed'} variants={variants}>
			{props.children}
		</SingleMusicItemStyled>
	);
};

export default SingleMusicItem;
