import styled from 'styled-components';

export const LoginComponentStyled = styled.div`
	position: relative;
	width: 60vw;
	height: 100vh;
	display: flex;
	align-items: center;
`;

export const LoginFields = styled.div`
	min-height: 200px;
	width: 100%;
	display: flex;
	flex-direction: row;
	flex-wrap: wrap;
	background-color: ${({ theme }) => theme.beigeColor};

	h1 {
		font-size: 1.6rem;
		width: 100%;
		font-weight: bold;
		text-align: left;
		text-transform: uppercase;
		color: ${({ theme }) => theme.primaryDark};
	}

	input {
		width: 30%;
		margin: 30px;
		margin-left: 0px;
		height: 30px;
		border: none;
		border-bottom: 2px solid ${({ theme }) => theme.primaryDark};
	}
`;
