import React, { useEffect, useState } from 'react';
import { TracklistContainer } from './TrackList.styled';
import { FaPlay, FaPause } from 'react-icons/fa';
import { setAudioPlaying, setAudioPaused } from '../../actions/audioActions';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchPlaylist, PlaylistContext } from '../../data/playlist';
import styled from 'styled-components';
import { getTracks } from '../../actions/trackUploadActions';
import { getReleases } from '../../actions/releaseActions';

const Tracklist = (props) => {
	const [ currentList, setCurrentList ] = useState([]);
	const [ currentTrack, setCurrentTrack ] = useState(0);

	// const handleFetchData = () => {
	// 	props.getReleases();
	// 	const tracks = props.release.releases;
	// };

	const handleChangeTrack = (id) => {
		setCurrentTrack(id);
	};

	const handleNextTrack = () => {
		if (currentTrack === props.playlist.tracks.length - 1) {
			setCurrentTrack(0);
			return;
		}
		setCurrentTrack(currentTrack + 1);
	};

	const handlePrevTrack = () => {
		if (currentTrack === 0) {
			setCurrentTrack(props.playlist.tracks.length - 1);
			return;
		}
		setCurrentTrack(currentTrack - 1);
	};

	useEffect(
		() => {
			setCurrentList(props.playlist.tracks);

			return serCurrentList([]);
		},
		[ props.playlist ]
	);

	const Loading = styled.div`
		position: absolute;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);
	`;

	return (
		<PlaylistContext.Provider>
			<TracklistContainer>
				<h1>{props.playlist.name}</h1>
				<h2>feb 2020</h2>
				<h3>featured tracks</h3>
				<ul>
					{props.playlist.tracks.map((track, index) => (
						<li key={track._id}>
							<p>
								{index !== currentTrack ? (
									<FaPlay
										onClick={handleChangeTrack(index)}
										style={{ cursor: 'pointer', marginRight: '10px' }}
									/>
								) : (
									<FaPause
										onClick={props.setAudioPaused}
										style={{ cursor: 'pointer', marginRight: '10px' }}
									/>
								)}
								{track.artistName + ' - ' + track.trackName}
							</p>
							<a>buy track</a>
						</li>
					))}
				</ul>
			</TracklistContainer>
		</PlaylistContext.Provider>
	);
};

Tracklist.propTypes = {
	setAudioPlaing: PropTypes.func,
	setAudioPaused: PropTypes.func,
	getTracks: PropTypes.func.isRequired,
	track: PropTypes.object,
	getReleases: PropTypes.func.isRequired,
	release: PropTypes.object,
	audio: PropTypes.object,
	playing: PropTypes.bool,
	currentTrack: PropTypes.object
};

const mapStateToProps = (state) => ({ audio: state.audio, track: state.track, release: state.release });

export default connect(mapStateToProps, { setAudioPlaying, setAudioPaused, getTracks, getReleases })(Tracklist);
