import { useEffect, useState } from 'react';
import {
	Button,
	Form,
	FormGroup,
	Label,
	Input,
	Dropdown,
	DropdownToggle,
	DropdownMenu,
	DropdownItem
} from 'reactstrap';
import Styled from 'styled-components';
import { addRelease, getReleases } from '../../actions/releaseActions';
import { getTracks } from '../../actions/trackUploadActions';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { artists } from '../../data/artists';

const ButtonContainer = Styled.div`
  height: 150px;
  width: 100%;
  position:absolute;
  bottom: 0;
  left:0;
  right: 0;

  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
`;

const FormContainer = Styled.div`
position: relative;
  height: 100%
  width: 100%;
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  justify-content: space-between;
  flex-wrap: wrap;
  padding: 100px;
`;

const UploadRelease = (props) => {
	const [ name, setName ] = useState('');
	const [ artists, setArtists ] = useState([]);
	const [ tracks, setTracks ] = useState([]);
	const [ artwork, setArtwork ] = useState('');
	const [ uploadedTracks, setUploadedTracks ] = useState([]);
	const [ dropdownOpen, setDropdownOpen ] = useState(false);

	const toggle = () => setDropdownOpen((prevState) => !prevState);

	useEffect(() => {
		clearForm();
		props.getTracks();
		setUploadedTracks(props.track.tracks);
	}, []);

	useEffect(
		() => {
			console.log(uploadedTracks);
		},
		[ name ]
	);

	const clearForm = () => {
		setName('');
		setArtists([]);
		setTracks([]);
		setArtwork('');
	};

	const onSubmit = (e) => {
		e.preventDefault();

		let formData = new FormData();

		formData.append('name', name);
		formData.append('artists', artists);
		formData.append('tracks', tracks);
		formData.append('artwork', artwork);

		props.addRelease(formData);

		clearForm();
	};

	return (
		<Form onSubmit={onSubmit} action='/cms' style={{ height: '100%', width: '100%' }}>
			<FormContainer>
				<FormGroup>
					<Label
						style={{
							padding: '5px',
							fontSize: '2rem',
							textAlign: 'left',
							color: 'white'
						}}
						htmlFor='name'>
						Release Title
					</Label>
					<Input type='text' name='name' value={name} onChange={(e) => setName(e.target.value)} />
				</FormGroup>
				<FormGroup>
					<Label
						style={{
							padding: '5px',
							fontSize: '2rem',
							textAlign: 'left',
							color: 'white'
						}}
						htmlFor='artists'>
						artists
					</Label>
					<Input
						type='text'
						name='artists'
						value={artists}
						onChange={(e) => setArtists(...artists, e.target.value)}
					/>
					{artists ? (
						artists.map((artist) => {
							<p>artist.name</p>;
						})
					) : null}
				</FormGroup>
				<FormGroup>
					<Label
						style={{
							padding: '5px',
							fontSize: '2rem',
							textAlign: 'left',
							color: 'white'
						}}
						htmlFor='tracks'>
						tracks
					</Label>
					<Dropdown isOpen={dropdownOpen} toggle={toggle}>
						<DropdownToggle caret>Dropdown</DropdownToggle>
						<DropdownMenu>
							<DropdownItem header>Header</DropdownItem>
							<DropdownItem>Some Action</DropdownItem>
							<DropdownItem text>Dropdown Item Text</DropdownItem>
							<DropdownItem disabled>Action (disabled)</DropdownItem>
							<DropdownItem divider />
							<DropdownItem>Foo Action</DropdownItem>
							<DropdownItem>Bar Action</DropdownItem>
							<DropdownItem>Quo Action</DropdownItem>
						</DropdownMenu>
					</Dropdown>
					<Input
						type='text'
						name='tracks'
						value={tracks}
						onChange={(e) => setTracks(...tracks, e.target.value)}
					/>
					{tracks ? (
						tracks.map((track) => {
							<p>track.name</p>;
						})
					) : null}
				</FormGroup>
				<FormGroup>
					<Label
						style={{
							padding: '5px',
							fontSize: '2rem',
							textAlign: 'left',
							color: 'white'
						}}
						htmlFor='artwork'>
						Artwork
					</Label>
					<Input type='text' name='artwork' value={artwork} onChange={(e) => setArtwork(e.target.value)} />
				</FormGroup>
			</FormContainer>

			<ButtonContainer>
				<Button className='btn-lg' style={{ margin: '20px' }} type='submit'>
					add item
				</Button>

				<Button className='btn-lg' style={{ margin: '20px' }} onClick={clearForm}>
					Clear Form
				</Button>
			</ButtonContainer>
		</Form>
	);
};

UploadRelease.propTypes = {
	setAudioPlaing: PropTypes.func,
	setAudioPaused: PropTypes.func,
	getReleases: PropTypes.func,
	addRelease: PropTypes.func,
	playing: PropTypes.bool,
	tracks: PropTypes.object,
	release: PropTypes.object,
	getTracks: PropTypes.func
};

const mapStateToProps = (state) => ({ release: state.release, track: state.track });

export default connect(mapStateToProps, { addRelease, getReleases, getTracks })(UploadRelease);
