import { motion } from 'framer-motion';
import { Fragment, useState, lazy, Suspense, useEffect } from 'react';
import { SingleMusicContainerStyled } from './SingleMusicItem.styled';
import Tracklist from './TrackList';
import { Container } from './SingleMusicItem.styled';
import PropTypes from 'prop-types';
import { withRouter, useParams } from 'react-router-dom';
import { connect } from 'react-redux';
import { setReleasePlaylist, getReleases } from '../../actions/releaseActions';
import { PageContainer } from '../Basic/PageContainer.styled';

const renderLoader = () => <p>Loading</p>;

const variants = {
	open: {
		opacity: 1,
		x: 0,
		transition: {
			type: 'tween',
			stiffness: 0,
			restDelta: 10,
			mass: 0.5
		}
	},
	closed: {
		opacity: 0,
		x: '100%',
		transition: {
			x: { type: 'spring', stiffness: 100 },
			damping: 10,
			mass: 0.1,
			when: 'beforeChildren'
		}
	}
};

const SingleMusicItemContainer = (props) => {
	const [ open, setOpen ] = useState(false);
	const [ currentPlaylist, setCurrentPlaylist ] = useState([]);

	const { id } = useParams();

	useEffect(() => {
		props.getReleases();
	}, []);

	useEffect(
		() => {
			console.log(id);
			console.log(props.release);
			props.setReleasePlaylist(id);
			setCurrentPlaylist(id);
		},
		[ id ]
	);
	return (
		<SingleMusicContainerStyled>
			<h3 onClick={() => setOpen(false)}>back</h3>

			<Container>
				{currentPlaylist ? <img src={currentPlaylist.artwork} /> : null}
				<Tracklist playlist={currentPlaylist} />
			</Container>
		</SingleMusicContainerStyled>
	);
};

SingleMusicItemContainer.propTypes = {
	setReleasePlaylist: PropTypes.func.isRequired,
	getReleases: PropTypes.func.isRequired,

	release: PropTypes.object
};

const mapStateToProps = (state) => ({ release: state.release });

export default withRouter(connect(mapStateToProps, { getReleases, setReleasePlaylist })(SingleMusicItemContainer));
