import { SET_AUDIO_PLAYING, SET_AUDIO_PAUSED, AUDIO_PLAYING, AUDIO_PAUSED } from '../actions/types';

const initialState = {
	items: [],
	singleItem: {},
	playing: false
};
// export items in current state + changes
export default function(state = initialState, action) {
	switch (action.type) {
		case GET_ITEMS:
			return {
				...state, // return state of items
				items: action.payload,
				loading: false
			};
		case GET_SINGLE_ITEM:
			return {
				...state, // return state of items
				singleItem: state.items.filter((item) => item._id === action.payload),
				loading: false
			};
		case DELETE_ITEM:
			return {
				...state,
				items: state.items.filter((item) => item._id !== action.payload) //return state without selected id
			};
		case ADD_ITEM:
			return {
				...state,
				items: [ action.payload, ...state.items ] // copy state and return with new item
			};
		case ITEMS_LOADING:
			return {
				...state,
				loading: true
			};
		case SET_AUDIO_PLAYING:
			return {
				...state,
				playing: true
			};
		case SET_AUDIO_PAUSED:
			return {
				...state,
				playing: false
			};

		default:
			return state;
	}
}
