import { motion } from 'framer-motion';
import { SingleMusicItemStyled } from './Music.styled';

const variants = {
	open: { opacity: 1, x: 0 },
	closed: { opacity: 0, x: '-100%' }
};

const SingleMusicItem = (props) => {
	return (
		<SingleMusicItemStyled animate={isOpen ? 'open' : 'closed'} variants={variants}>
			<Toggle onClick={() => setIsOpen(!isOpen)} />
			<Items />
		</SingleMusicItemStyled>
	);
};

export default SingleMusicItem;
