import { ThemeProvider } from 'styled-components';
import {GlobalStyles} from './GlobalStyles'
import { Theme } from './Theme'
import { BrowserRouter as Router, Route, Switch, NavLink, withRouter, Link } from 'react-router-dom';
import NavBar from './components/NavBar/NavBar';
import FullPageComponent from './components/FullPageComponent/FullPageComponent';
import FullPageRow from './components/FullPageComponent/FullPageRow';
import About from './components/About/About';
import Release from './components/Release/Release';
import {useEffect, useState} from 'react';
import {aboutItems} from './data/about';
import {releaseItems} from './data/releases';
import ReleaseSwiper from './components/ReleaseSwiper/ReleaseSwiper';

function App() {
  const [releases, setReleases] = useState([])
  const [abouts, setAbouts] = useState([])

  useEffect(() => {
    const these = releaseItems.slice(0,2)
    setReleases([...these])
    setAbouts([aboutItems])
  }, [releaseItems, aboutItems])
  return (
    <ThemeProvider theme={Theme}>
      <Router>
        <Switch>
          <>
          <GlobalStyles />
            <div className="App">
              <NavBar signedIn={false}/>
             <FullPageComponent>
             {abouts ? 
             <FullPageRow>
             {abouts.map(item => (
               <Release key={item.id} header={item.header} bio={item. bio}/>
             ))}
             </FullPageRow>
             : <p>there's no abouts</p>}
            {releases ?
             <FullPageRow>
             {releases.map(release => (
               <Release key={release.id} header={release.header} artwork={release. artwork} title={release.title} artists={release.artists}/>
             ))}
             </FullPageRow>
             : <p>there's no releaes</p>}
             </FullPageComponent>

            </div>
          </>
        </Switch>
      </Router>

    </ThemeProvider>
  );
}

export default App;
