import styled from 'styled-components';

export const FullPageComponentStyled = styled.div`
	position: relative;
	top: ${({ theme }) => theme.menuHeight};

	min-height: 1px;
	height: 100%;
	width: 100vw;
	display: grid;
	grid-template-rows: 3fr 1fr;
	padding: 0% 5% 0% 5%;
	align-items: center;
	justify-content: center;
	background-color: ${({ theme }) => theme.white};
	overflow: hidden;
`;

export const FullPageColumnStyled = styled.div`
	position: relative;
	height: 50%;
	min-height: 1px;
	width: 100%;
	display: flex;
	flex-direction: row;
	align-items: center;
	justify-content: center;
	background-color: ${({ theme }) => theme.white};
`;

export const FullPageRowStyled = styled.div`
	position: relative;
	height: 100%;
	min-height: 1px;
	width: 100%;
	display: grid;
	grid-template-columns: 1fr 1fr 1fr;
	gap: 10px 40px;
	align-items: flex-start;
	justify-content: center;
	background-color: ${({ theme }) => theme.white};
`;
