import React, { useState } from 'react';
import styled from 'styled-components';
import PropsTypes from 'prop-types';
import { Audio } from './Audio';

export function Top(props) {
	const Container = styled.div`
		border-radius: 5px 5px 0 0;
		height: 80px;
		padding: 2rem;
		z-index: 101;
		display: flex;
		flex-wrap: nowrap;
		color: #27117c;
		box-sizing: border-box;
		background-image: linear-gradient(to bottom, #ebe6f8, #ece5f6, #ede4f5, #eee4f3, #efe3f1);
	`;

	const Cover = styled.img`
		width: 135px;
		height: 135px;
		border-radius: 5px;
		margin-right: 1rem;
	`;

	const Info = styled.div`
		display: flex;
		flex-direction: row;
	`;

	const Title = styled.p`
		font-size: 1rem;
		font-weight: 600;
		white-space: nowrap;
	`;

	const Author = styled.p`
		font-size: 1rem;
		font-weight: 600;
		text-transform: uppercase;

		white-space: nowrap;
	`;

	const Controls = styled.div`margin-top: 0rem;`;

	return (
		<Container>
			{/* <Cover src={props.track.cover} alt='cover' /> */}
			<Controls>
				<Audio url={props.track.url} />
			</Controls>
			<Info>
				<Author>{props.track.author} - </Author>
				<Title>{props.track.title}</Title>
			</Info>
		</Container>
	);
}

Top.propTypes = {
	track: PropsTypes.object
};
