import styled from 'styled-components';

export const FullPageComponentStyled = styled.div`
  position: absolute;
  top:0;
  left: 0;
  right: 0; 
  bottom: 0;
  min-height: 1px;
  height: 100vh;
  width: 100vw;
  display: grid;
  grid-template-rows: 1fr 1fr;
  align-items:center;
  justify-content:center;
  background-color: ${({theme}) => theme.white};
`;

export const FullPageColumnStyled = styled.div`
  position: relative;
  height: 50%;
  min-height: 1px;
  width:30%;
  display: flex;
  flex-direction:row;
  align-items:center;
  justify-content:center;
  background-color: ${({theme}) => theme.white};
`;

export const FullPageRowStyled = styled.div`
  position: relative;
  height: 50%;
  min-height: 1px;
  width:100%;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  align-items:center;
  justify-content:center;
  background-color: ${({theme}) => theme.white};
`;
