import styled from 'styled-components';

export const AboutFooterItemStyled = styled.div`
	display: flex;
	flex-direction: column;
	width: 100%;

	position: relative;
	margin: a auto;
	padding: 0;

	h1 {
		font-size: ${({ theme }) => theme.header};
		text-align: left;
		text-transform: uppercase;
		margin: 0;
		margin-bottom: 10px;
	}

	h2 {
		font-size: ${({ theme }) => theme.subHead};
		text-align: left;
		text-transform: capitalize;
		margin: 0;
		margin-bottom: 20px;
	}

	p {
		font-size: ${({ theme }) => theme.paragraph};
		text-align: left;
		text-transform: capitalize;
		margin: 0;
		margin-bottom: 10px;
	}
`;
