import React, { Component } from 'react';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';
import Styled from 'styled-components';

const AppContainer = Styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: row;

`;
const Menu = Styled.div`
  height: 100%
  width: 30%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 10px;
`;

const FormContainer = Styled.div`
  height: 100%
  width: 70%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  padding: 10px;
`;

export class UploadTrack extends Component {
	constructor(props) {
		super(props);
		this.state = {
			id: null,
			productionCompany: '',
			modal: false,
			brand: '',
			agency: '',
			title: '',
			director: '',
			gallery: '',
			video: '',
			favourite: true
		};
	}

	componentDidMount() {
		this.clearForm();
	}

	selectItem = (item) => {
		this.setState({
			id: item._id,
			productionCompany: item.productionCompany,
			brand: item.brand,
			agency: item.agency,
			title: item.title,
			director: item.director,
			video: item.video,
			favourite: item.favourite,
			gallery: item.gallery
		});
	};
	toggle = () => {
		this.setState({
			modal: !this.state.modal
		});
	};

	// clear form function actioned when clear form button selected and on form
	// submit
	clearForm = () => {
		this.setState({
			id: null,
			productionCompany: '',
			brand: '',
			agency: '',
			title: '',
			director: '',
			video: '',
			favourite: false,
			gallery: ''
		});
	};

	// retrieve main image from single image select modal
	imageSelect = (image) => {
		this.setState({ gallery: image });
	};

	// retrieve video from video select modal
	videoSelect = (vid) => {
		this.setState({ video: vid });
	};

	onChange = (e) => {
		switch (e.target.name) {
			case 'favourite':
				this.setState({
					favourite: !this.state.favourite // flip boolean value
				});
				break;
			default:
				this.setState({
					[e.target.name]: e.target.value
				});
		}
	};

	onSubmit = (e) => {
		e.preventDefault();
		const { productionCompany, brand, agency, title, director, gallery, video, favourite } = this.state;

		// this.composeGalleryArray(gallery);
		// const galleryLen = gallery.length;

		let formData = new FormData();

		formData.append('productionCompany', productionCompany);
		formData.append('brand', brand);
		formData.append('agency', agency);
		formData.append('title', title);
		formData.append('director', director);
		formData.append('video', video);
		formData.append('favourite', favourite);
		formData.append('gallery', gallery);
		// for (var y = 0; y < galleryLen; y++) {
		//   formData.append("gallery[]", gallery[y]);
		// }

		// close modal.
		this.toggle();

		this.props.addItem(formData);

		this.clearForm();
	};

	// retrieve images from gallery modal and set to current project state
	submitGallery = (galleryImages) => {
		this.setState({ gallery: galleryImages });
		this.setState({
			modal: false
		});
	};

	composeGalleryArray = (items) => {
		this.setState({
			gallery: [ ...this.state.gallery, ...items ]
		});
	};

	render() {
		const { items } = this.props;
		const { _id, productionCompany, brand, agency, title, director, gallery, video } = this.state;
		return (
			<AppContainer>
				<Menu>
					<Button className='admin-button' onClick={this.clearForm}>
						Clear Form
					</Button>
				</Menu>
				<FormContainer>
					<Form onSubmit={this.onSubmit} action='/'>
						<FormGroup>
							<Label
								style={{
									padding: '5px'
								}}
								htmlFor='brand'>
								brand
								<Input type='text' name='brand' value={brand} onChange={this.onChange} />
							</Label>
						</FormGroup>
						<FormGroup>
							<Label
								style={{
									padding: '5px'
								}}
								htmlFor='agency'>
								agency
								<Input type='text' name='agency' value={agency} onChange={this.onChange} />
							</Label>
						</FormGroup>
						<FormGroup>
							<Label
								style={{
									padding: '5px'
								}}
								htmlFor='title'>
								title
								<Input type='text' name='title' value={title} onChange={this.onChange} />
							</Label>
						</FormGroup>

						<FormGroup>
							<Label
								style={{
									padding: '5px'
								}}
								htmlFor='director'>
								director
								<Input type='text' name='director' value={director} onChange={this.onChange} />
							</Label>
						</FormGroup>

						<FormGroup>
							<Label
								style={{
									padding: '5px'
								}}
								htmlFor='productionCompany'>
								production company
								<Input
									type='text'
									name='productionCompany'
									value={productionCompany}
									onChange={this.onChange}
								/>
							</Label>
						</FormGroup>
						<FormGroup>
							<Button className='admin-button' type='submit'>
								add item
							</Button>
							{/* <Button onClick={() => this.state._id === null ? null : this.props.deleteMenu(_id)}className="admin-button" type="submit">{_id === null
                  ? "Add Menu"
                  : "Edit Menu"}</Button> */}
						</FormGroup>
					</Form>
				</FormContainer>
			</AppContainer>
		);
	}
}

export default UploadTrack;
