import { Fragment, useState } from 'react';
import { MusicStyled, MusicItem, ContentContainer } from './Music.styled';
import SingleMusicItem from './SingleMusicItem';
import Background from '../../components/Basic/Background/Background';
import { loremIpsum, Avatar, name, surname, fullname, username } from 'react-lorem-ipsum';
import { releaseItems } from '../../data/releases';

const Music = () => {
	const [ releases, setReleases ] = useState(releaseItems);
	const [ isOpen, setIsOpen ] = useState(true);
	// var item = document.getElementsByTagName('MAIN')[0];

	// window.addEventListener('wheel', function(e) {
	// 	if (e.deltaY > 0) item.scrollLeft += 100;
	// 	else item.scrollLeft -= 100;
	// });

	return (
		<Fragment>
			<Background
				style={{ backgroundSize: '80% 70% !important' }}
				background='https://cdn.mems.lol/outboard/leaves-image.png'
			/>
			<SingleMusicItem open={isOpen} isOpen={isOpen} setIsOpen={setIsOpen}>
				<h1>cunt</h1>
			</SingleMusicItem>
			<MusicStyled>
				<h1>PREVIOUS RELEASES</h1>
				<ContentContainer>
					{releases.map((release) => (
						<MusicItem onClick={setIsOpen} animate={{ opacity: 1 }}>
							<img src={release.artwork} />
							<div>
								<h2>
									{release.title} {release.date}
								</h2>
							</div>
						</MusicItem>
					))}
				</ContentContainer>
			</MusicStyled>
		</Fragment>
	);
};

export default Music;
