import styled from 'styled-components';

export const SubscribeStyled = styled.div`
	display: flex;
	flex-direction: column;
	justify-content: flex-start;
	align-items: center;

	p {
		font-size: 1rem;
	}

	h3 {
		font-weight: bold;
		font-size: 1rem;
	}
	button {
		background-color: ${({ theme }) => theme.primaryLight};
		text-transform: uppercase;
		font-weight: bold;
		font-size: 1.6rem;
		text-align: center;
	}
`;
