import { Fragment, useState } from 'react';
import { ThemeProvider } from 'styled-components';
import { GlobalStyles } from './GlobalStyles';
import { Theme } from './Theme';
import { BrowserRouter as Router, Route, Switch, NavLink, withRouter, Link } from 'react-router-dom';
import NavBar from './components/NavBar/NavBar';
import Content from './components/Content/Content';
import ReactAudioPlayer from 'react-audio-player';
import { Player } from './components/Player/Player';
import Portal from './components/Basic/Portal';
import { setAudioPlaying, setAudioPaused } from './actions/audioActions';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import MainSite from './MainSite';

import Home from './pages/Home/Home';
import About from './pages/About/About';
import Login from './pages/Login/Login';
import Artists from './pages/Artists/Artists';
import Music from './pages/Music/Music';

function App(props) {
	const [ signedIn, setSignedIn ] = useState(true);
	const [ playing, setPlaying ] = useState(true);

	return (
		<div>
			<MainSite />
			{props.audio.playing ? (
				<Portal id='audioPortal'>
					<Player />
				</Portal>
			) : null}
		</div>
	);
}

App.propTypes = {
	setAudioPlaing: PropTypes.func,
	setAudioPaused: PropTypes.func,
	audioPlaying: PropTypes.bool
};

const mapStateToProps = (state) => ({ audio: state.audio });

export default withRouter(connect(mapStateToProps, { setAudioPlaying, setAudioPaused })(App));
