import styled from 'styled-components';
import { motion } from 'framer-motion';

export const MusicStyled = styled.div`
	height: calc(100vh - 240px);
	width: 100vw;
	margin: 120px 0px 120px 0px;
	display: grid;
	grid-template-columns: 1fr 5fr;
	grid-column-gap: 300px;
	align-items: center;

	h1 {
		margin: 0 auto;
		margin-bottom: 200px;
		width: 300px;
	}
`;

export const MusicItem = styled(motion.div)`
display: flex;
  height: 700px;
  width: 400px;
  margin: 40px;
  opacity: 0;
  align-items:flex-start;
  flex-wrap:wrap;

  img {
    display: block;
    width: 100%;
   margin-bottom: 10px;
    min-width: 400px;
  }

  div {
    margin-top: 5px auto;
    height: 100px;
    display: flex;
    align-items: flex-start;
    justify-content: flex-start;
    flex-direction: column;
  }

  h2 {
    font-size: 1.4rem;
    font-weight: bold;
    text-transform: uppercase;
    text-align: left;
  }

 p {
    font-size: 1rem;
    text-align: left;
  }

`;

export const ContentContainer = styled.div`
	height: 100%;
	min-width: 100%;
	width: auto;
	overflow-y: scroll;
	overflow-x: scroll;
	display: flex;
	z-index: 10;
	flex: 10 0 auto;
	align-items: center;
	justify-content: flex-start;
`;
