import FullPageComponent from '../FullPageComponent/FullPageComponent';
import FullPageRow from '../FullPageComponent/FullPageRow';
import About from '../About/About';
import Release from '../Release/Release';
import { HomeStyled } from './Home.styled';
import { useEffect, useState } from 'react';
import { aboutItems } from '../../data/about';
import { releaseItems } from '../../data/releases';
import Subscribe from '../Subscribe/Subscribe';

const Home = (props) => {
	const [ releases, setReleases ] = useState([]);
	const [ abouts, setAbouts ] = useState([]);

	useEffect(
		() => {
			const these = releaseItems.slice(0, 2);
			setReleases([ ...these ]);
			setAbouts([ aboutItems ]);
		},
		[ releaseItems, aboutItems ]
	);

	return (
		<HomeStyled>
			<FullPageComponent>
				{releases ? (
					<FullPageRow>
						{releases.map((release) => (
							<Release
								key={release.id}
								header={release.header}
								artwork={release.artwork}
								title={release.title}
								artists={release.artists}
								date={release.date}
							/>
						))}
						<Subscribe />
					</FullPageRow>
				) : (
					<p>there's no releaes</p>
				)}
				{aboutItems ? (
					<FullPageRow>
						{aboutItems.map((item) => <About key={item.id} header={item.header} bio={item.bio} />)}
					</FullPageRow>
				) : (
					<p>there's no abouts</p>
				)}
			</FullPageComponent>
		</HomeStyled>
	);
};

export default Home;
