import styled from 'styled-components';

export const ReleaseStyled = styled.div`
  width: 90%;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  margin: 0 auto;


  h1 {
    text-align: left;
    margin-top: 5px;
    margin-bottom: 5px;
    width: 100%;
  }

  h2 {
    text-align: left;
    margin-top: 5px;
    margin-bottom: 5px;
    width: 100%;
  }

  h3 {
    text-align: left;
    margin-top: 5px;
    margin-bottom: 5px;
    flex-direction: row;
  }

  div {
    width: 100%;
    max-width: 100%;
    max-height:100%;
    margin: 0 auto;
    overflow: hidden;

    &:after {
      content:'';
      display: block;
      padding-bottom: 100%;
    }
  }

  img {
    max-width: 100%;
  }

`;
