import { motion } from 'framer-motion';
import { Fragment, useState, lazy, Suspense, useEffect } from 'react';
import { SingleMusicContainerStyled } from './SingleMusicItem.styled';
import Tracklist from './TrackList';
import { Container } from './SingleMusicItem.styled';

const renderLoader = () => <p>Loading</p>;

const variants = {
	open: {
		opacity: 1,
		x: 0,
		transition: {
			type: 'tween',
			stiffness: 0,
			restDelta: 10,
			mass: 0.5
		}
	},
	closed: {
		opacity: 0,
		x: '100%',
		transition: {
			x: { type: 'spring', stiffness: 100 },
			damping: 10,
			mass: 0.1,
			when: 'beforeChildren'
		}
	}
};

const SingleMusicItemContainer = (props) => {
	const [ open, setOpen ] = useState(false);

	useEffect(() => {
		setOpen(true);

		return setOpen(false);
	});
	return (
		<SingleMusicContainerStyled animate={open ? 'open' : 'closed'} variants={variants}>
			<Suspense fallback={renderLoader()}>
				<SingleMusicItemContainer open={open} isOpen={open} setIsOpen={setOpen}>
					<h3 onClick={setOpen(false)}>back</h3>

					<Container animate={{ opacity: 1 }}>
						{currentPlaylist ? <img src={currentPlaylist.artwork} /> : null}
						<Tracklist />
					</Container>
				</SingleMusicItemContainer>
			</Suspense>
		</SingleMusicContainerStyled>
	);
};

export default SingleMusicItemContainer;
