import React, { Component } from 'react';
import { Button, Form, FormGroup, Label, Input } from 'reactstrap';
import Styled from 'styled-components';

const AppContainer = Styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: row;

`;
const ButtonContainer = Styled.div`
  height: 150px;
  width: 100%;
  position:absolute;
  bottom: 0;
  left:0;
  right: 0;

  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
`;

const FormContainer = Styled.div`
position: relative;
  height: 100%
  width: 100%;
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  justify-content: space-between;
  flex-wrap: wrap;
  padding: 100px;
`;

export class UploadTrack extends Component {
	constructor(props) {
		super(props);
		this.state = {
			id: null,
			productionCompany: '',
			modal: false,
			brand: '',
			agency: '',
			title: '',
			director: '',
			gallery: '',
			video: '',
			favourite: true
		};
	}

	componentDidMount() {
		this.clearForm();
	}

	selectItem = (item) => {
		this.setState({
			id: item._id,
			productionCompany: item.productionCompany,
			brand: item.brand,
			agency: item.agency,
			title: item.title,
			director: item.director,
			video: item.video,
			favourite: item.favourite,
			gallery: item.gallery
		});
	};
	toggle = () => {
		this.setState({
			modal: !this.state.modal
		});
	};

	// clear form function actioned when clear form button selected and on form
	// submit
	clearForm = () => {
		this.setState({
			id: null,
			artist: '',
			trackName: '',
			artwork: ''
		});
	};

	// retrieve main image from single image select modal
	imageSelect = (image) => {
		this.setState({ gallery: image });
	};

	// retrieve video from video select modal
	videoSelect = (vid) => {
		this.setState({ video: vid });
	};

	onChange = (e) => {
		switch (e.target.name) {
			case 'favourite':
				this.setState({
					favourite: !this.state.favourite // flip boolean value
				});
				break;
			default:
				this.setState({
					[e.target.name]: e.target.value
				});
		}
	};

	onSubmit = (e) => {
		e.preventDefault();
		const { productionCompany, brand, agency, title, director, gallery, video, favourite } = this.state;

		// this.composeGalleryArray(gallery);
		// const galleryLen = gallery.length;

		let formData = new FormData();

		formData.append('productionCompany', productionCompany);
		formData.append('brand', brand);
		formData.append('agency', agency);
		formData.append('title', title);
		formData.append('director', director);
		formData.append('video', video);
		formData.append('favourite', favourite);
		formData.append('gallery', gallery);
		// for (var y = 0; y < galleryLen; y++) {
		//   formData.append("gallery[]", gallery[y]);
		// }

		// close modal.
		this.toggle();

		this.props.addItem(formData);

		this.clearForm();
	};

	// retrieve images from gallery modal and set to current project state
	submitGallery = (galleryImages) => {
		this.setState({ gallery: galleryImages });
		this.setState({
			modal: false
		});
	};

	composeGalleryArray = (items) => {
		this.setState({
			gallery: [ ...this.state.gallery, ...items ]
		});
	};

	render() {
		const { items } = this.props;
		const { _id, productionCompany, brand, agency, title, director, gallery, video } = this.state;
		return (
			<Form onSubmit={this.onSubmit} action='/cms' style={{ height: '100%', width: '100%' }}>
				<FormContainer>
					<FormGroup>
						<Label
							style={{
								padding: '5px',
								fontSize: '2rem',
								color: 'white'
							}}
							htmlFor='brand'>
							Artist Name
						</Label>
						<Input type='text' name='brand' value={brand} onChange={this.onChange} />
					</FormGroup>
					<FormGroup>
						<Label
							style={{
								padding: '5px',
								fontSize: '2rem',
								color: 'white'
							}}
							htmlFor='brand'>
							Track Name
						</Label>
						<Input type='text' name='brand' value={brand} onChange={this.onChange} />
					</FormGroup>
					<FormGroup>
						<Label
							style={{
								padding: '5px',
								fontSize: '2rem',
								color: 'white'
							}}
							htmlFor='brand'>
							url
						</Label>
						<Input type='text' name='brand' value={brand} onChange={this.onChange} />
					</FormGroup>
					<FormGroup>
						<Label
							style={{
								padding: '5px',
								fontSize: '2rem',
								color: 'white'
							}}
							htmlFor='brand'>
							Artwork
						</Label>
						<Input type='text' name='brand' value={brand} onChange={this.onChange} />
					</FormGroup>
				</FormContainer>

				<ButtonContainer>
					<Button className='btn-lg' style={{ margin: '20px' }} type='submit'>
						add item
					</Button>

					<Button className='btn-lg' style={{ margin: '20px' }} onClick={this.clearForm}>
						Clear Form
					</Button>
				</ButtonContainer>
			</Form>
		);
	}
}

export default UploadTrack;
