import { Fragment, useState } from 'react';
import { ThemeProvider } from 'styled-components';
import { GlobalStyles } from './GlobalStyles';
import { Theme } from './Theme';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import NavBar from '../NavBar/NavBar';
import Content from '../Content/Content';
import { setAudioPlaying, setAudioPaused } from './components/actions/audioActions';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

import Home from '../../pages/Home/Home';
import About from '../../pages/About/About';
import Login from '../../pages/Login/Login';
import Artists from '../../pages/Artists/Artists';
import Music from '../../pages/Music/Music';

function App() {
	const [ signedIn, setSignedIn ] = useState(true);
	const [ playing, setPlaying ] = useState(true);

	return (
		<ThemeProvider theme={Theme}>
			<Fragment>
				<GlobalStyles />
				<div className='App'>
					<Router>
						<NavBar signedIn={signedIn} setSignedIn={setSignedIn} />
						<Content>
							<Switch>
								<Route exact path='/' render={(props) => <Home />} />
								<Route path='/about' render={(props) => <About full={false} />} />
								<Route path='/user' render={(props) => <Login />} />
								<Route path='/artists' render={(props) => <Artists />} />
								<Route path='/music' render={(props) => <Music playing={playing} />} />
								{/* <Route exact path='/artists/:id' render={(props) => <SingleArtist />} /> */}
							</Switch>
						</Content>
					</Router>
				</div>
			</Fragment>
		</ThemeProvider>
	);
}

App.propTypes = {
	setAudioPlaing: PropTypes.func,
	setAudioPaused: PropTypes.func,
	audioPlaying: PropTypes.bool
};

const mapStateToProps = (state) => ({ audio: state.audio });

export default connect(mapStateToProps, { setAudioPlaying, setAudioPaused })(App);
