import styled from 'styled-components';
import { motion } from 'framer-motion';

export const SalesPitchContainer = styled.div`
	position: relative;
	min-height: 100%;
	width: 100%;
	display: grid;
	grid-template-rows: 1fr 1fr 1fr 1fr;

	align-items: center;
	justify-content: center;
	background-color: ${({ theme }) => theme.white};
`;

export const SalesItem = styled(motion.div)`
  width: 100%;
  max-height: 150px;
  position: relative;

  svg {
    order: 1;
    width: 20%;
    height: auto;
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
  }

  div {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 100px;
    order:2;
    width: 80%;
    display: flex;
    flex-wrap: wrap;
    flex-direction: column;
    margin-right: 0 auto;

    h2 {
      font-weight: bold;
      width: 100%;
    }

    p {
      font-size: 1.1rem;
      width: 100%;
    }
  }
`;
