import styled from 'styled-components';
import { motion } from 'framer-motion';

export const MusicStyled = styled.div`
	height: calc(100vh - 240px);
	width: 100vw;
	margin: 120px 0px 120px 0px;

	display: grid;
	grid-template-columns: 1fr 4fr;
	grid-gap: 0;
	align-items: center;

	h1 {
		margin-bottom: 200px;
	}
`;

export const MusicItem = styled(motion.div)`
  max-height: 800px;
  max-width: 500px;
  opacity: 0;

  img {
    width: 100%;
    height: auto;
    max-width: 500px;
  }

  div {
    height: 100px;
    display: flex;
    align-items: center;
    justify-content: flex-start;
    flex-direction: column;
  }

  h2 {
    font-size: 1.4rem;
    font-weight: bold;
    text-transform: uppercase;
    text-align: left;
  }

 p {
    font-size: 1rem;
    text-align: left;
  }

`;
