import styled from 'styled-components';

export const AboutStyled = styled.div`
	display: flex;
	flex-direction: column;
	height: 150px;
	width: 80%;
	margin: 0 auto;
	flex-wrap: nowrap;
	overflow: hidden;
	background-color: ${({ theme }) => theme.primaryLight};

	h3 {
		text-align: left;
		color: ${({ theme }) => theme.primaryDark};
		font-size: 1.5rem;
		text-transform: uppercase;
		font-weight: bold;
		margin: 5px;
	}

	p {
		text-align: left;
		color: ${({ theme }) => theme.primaryDark};
		font-size: 1rem;
		text-transform: capitalize;
		font-weight: lighter;
		line-height: 1.3rem;
		margin: 5px;
	}
`;
