import styled from 'styled-components';

export const AboutStyled = styled.div`
  display: flex;
  flex-direction: column;
  height: 300px;
  width: 100%;
  margin: 0 auto;

  h3 {
    text-align: left;
    color: ${({theme}) => theme.primaryDark};
    font-size: 1.5rem;
    text-transform: uppercase;
    font-weight:bold;

  }

  h3 {
    text-align: left;
    color: ${({theme}) => theme.primaryDark};
    font-size: 1rem;
    text-transform: capitalize;
    font-weight:lighter;

  }
`;
