import { React, useEffect, useState } from 'react';
import { artists as artistData } from '../../data/artists';
import {
	ArtistsStyled,
	ArtistItem,
	ArtistPage,
	Container,
	Header,
	ContentContainer,
	SingleArtist
} from './Artists.styled';
import FullPageComponent from '../../components/FullPageComponent/FullPageComponent';
import FullPageRow from '../../components/FullPageComponent/FullPageRow';
import { loremIpsum, Avatar, name, surname, fullname, username } from 'react-lorem-ipsum';

const Artists = (props) => {
	const [ artists, setArtists ] = useState(artistData);
	const [ viewArtist, setViewArtist ] = useState(true);
	console.log(artists);
	return (
		<ArtistPage>
			<Header>
				<h1>featured artists</h1>
			</Header>
			{viewArtist ? (
				<ContentContainer>
					<SingleArtist>
						<div>
							<h1>otbd002-mar 2020</h1>
							<h1>{username()}</h1>
						</div>
						<div>
							<Avatar gender='all' className='avatar' width='350' height='350' alt='Avatar' />
						</div>
						<div>{loremIpsum({ p: 3, avgSentencesPerParagraph: 3 })}</div>
					</SingleArtist>
				</ContentContainer>
			) : (
				<ContentContainer>
					<Container>
						<h2>OTBD002 - MAR 2020</h2>
						<ArtistsStyled animate={{ opacity: 1 }}>
							{artists.map((artist) => (
								<ArtistItem>
									<Avatar gender='all' className='avatar' width='350' height='350' alt='Avatar' />
									<h3>{username()}</h3>
								</ArtistItem>
							))}
						</ArtistsStyled>
					</Container>
					<Container>
						<h2>OTBD001 - FEB 2020</h2>
						<ArtistsStyled animate={{ opacity: 1 }}>
							{artists.map((artist) => (
								<ArtistItem>
									<Avatar gender='all' className='avatar' width='350' height='350' alt='Avatar' />
									<h3>{username()}</h3>
								</ArtistItem>
							))}
						</ArtistsStyled>
					</Container>
					<Container>
						<h2>OTBD001 - FEB 2020</h2>
						<ArtistsStyled animate={{ opacity: 1 }}>
							{artists.map((artist) => (
								<ArtistItem>
									<Avatar gender='all' className='avatar' width='350' height='350' alt='Avatar' />
									<h3>{username()}</h3>
								</ArtistItem>
							))}
						</ArtistsStyled>
					</Container>
				</ContentContainer>
			)}
		</ArtistPage>
	);
};

export default Artists;
