import { Fragment, useState } from 'react';
import { ThemeProvider } from 'styled-components';
import { GlobalStyles } from './GlobalStyles';
import { Theme } from './Theme';
import MainSite from './components/MainSite/MainSite';
import { Player } from './components/Player/Player';
import { setAudioPlaying, setAudioPaused } from './actions/audioActions';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

function App(props) {
	const [ playing, setPlaying ] = useState(true);

	return (
		<ThemeProvider theme={Theme}>
			<Fragment>
				<GlobalStyles />
				<div className='App'>
					<MainSite playing={playing} />

					<Player playing={playing} />
				</div>
			</Fragment>
		</ThemeProvider>
	);
}

App.propTypes = {
	setAudioPlaing: PropTypes.func,
	setAudioPaused: PropTypes.func,
	audioPlaying: PropTypes.bool
};

const mapStateToProps = (state) => ({ audio: state.audio });

export default connect(mapStateToProps, { setAudioPlaying, setAudioPaused })(App);
