import {
	SET_AUDIO_PLAYING,
	SET_AUDIO_PAUSED,
	AUDIO_PLAYING,
	AUDIO_PAUSED,
	GET_TRACKS,
	NEXT_TRACK,
	PREV_TRACK,
	SET_CURRENT_TRACK_MOMENT,
	SET_CURRENT_TRACK_DURATION
} from '../actions/types';

const initialState = {
	playlist: [],
	currentTrack: 0,
	currentTrackDuration: 0,
	currentTrackMoment: 0,
	playing: true,
	playingTime: '0: 00'
};
// export items in current state + changes
const audioReducer = (state = initialState, action) => {
	switch (action.type) {
		case GET_TRACKS:
			return {
				...state, // return state of items
				playlist: action.payload,
				loading: false
			};
		case SET_AUDIO_PLAYING:
			return {
				...state,
				playing: true
			};
		case SET_AUDIO_PAUSED:
			return {
				...state,
				playing: false
			};
		case SET_CURRENT_TRACK_DURATION:
			return {
				...state,
				currentTrackDuration: action.payload
			};
		case SET_CURRENT_TRACK_MOMENT:
			return {
				...state,
				currentTrackMoment: action.payload
			};
		case NEXT_TRACK:
			return {
				...state,
				currentTrack: action.payload
			};
		case PREV_TRACK:
			return {
				...state,
				currentTrack: action.payload
			};
		default:
			return state;
	}
};

export default audioReducer;
